                        }

                        inter2.style.fill = sColor;
                        inter3.style.fill = sColor;

                        setReadableCode();
                    }

                    function clickInterLabelHomo() {
                        clickIntersection2('interLabelHomo', 'interLabelHomoVecto');
                    }

                    function clickInterHomoVecto() {
